mapper.select:
select *
from gxucpc.information
WHERE contestId = #{contestId}
order by informationId desc
limit #{offset}, #{count}

select *
from information
where clientNo = #{client}
  and contestId = #{id}

mapper.delete:
delete
from information
where informationId = #{id}
  and clientNo = #{client}

mapper.select:
select userName, userId
from information
where informationId = #{id}

select informationId
from information
where contestId = #{id}

select informationId
from information
where contestId = #{contestId}
  and userId = #{userId}
